import React from 'react';
import Link from '../Link';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Button } from '@material-ui/core';
import Hidden from '@material-ui/core/Hidden';
import { i18n, withTranslation } from '../i18n';

const useStyles = makeStyles((theme) => ({
	root: {},
	footer: {
		width: '100%'
	},
	mainContainer: {
		position: 'absolute',
		height: '200px',
		backgroundColor: '#384047'
	},
	link: {
		fontFamily: 'Roboto',
		fontSize: '0,75rem',
		color: 'white',
		textDecoration: 'none'
	},
	linkCopy: {
		fontWeight: '300',
		color: 'white',
		marginTop: '30px'
	},

	button: {
		fontFamily: 'Roboto',
		marginBottom: '20px',
		fontSize: '16px',
		textTransform: 'none',
		color: 'white',
		cursor: 'pointer',
		fontWeight: '500',
		minWidth: 5,
		'&:hover': {
			textDecoration: 'none'
		}
	}
}));

